import { Component } from '@angular/core';
import { Employee } from '../employee';
import { EmployeeService } from '../employee.service';

import { Router } from '@angular/router';
@Component({
  selector: 'app-employee-list',
  templateUrl: './employee-list.component.html',
  styleUrls: ['./employee-list.component.css']
})
export class EmployeeListComponent {

  employees: Employee[];

  constructor(private employeeService: EmployeeService,  private router: Router) {
    this.employees=[]
   }

  ngOnInit(): void {

    // this.employees = [
    //   { "id":1,fname: 'John', lname: 'Doe', email: 'john@example.com', salary: 50000, department: 'IT', designation: 'Developer' },

    // ];

    this.getEmployees();
  }

  getEmployees(){
    this.employeeService.getEmployeesList().subscribe(data => {this.employees = data;});


  }

  updateEmployee(id: number){
    this.router.navigate(['updating-by-id', id]);
  }




}
<br>
<br>
<!-- <h1>Employee List</h1> -->
<table style="margin-bottom: 50px;" class=" table table-responsive table-striped  border-dark abc">

    <thead class="table-dark thead-dark ">
        <tr>
            <!-- <th>Id</th> -->
            <th>First Name</th>
            <th>Last Name</th>
            <th>Email</th>
            <th>Salary</th>
            <th>Department</th>
            <th>Designation</th>
            <th>Joining Date</th>
            <th>Actions</th>
        </tr>

    </thead> 


    <tbody>
        <tr *ngFor = "let employee of employees">
            <!-- <td>{{employee.id}}</td> -->
            <td>{{employee.fname}}</td>
            <td>{{employee.lname}}</td>
            <td>{{employee.email}}</td>
            <td>{{employee.salary}}</td>
            <td>{{employee.department}}</td>
            <td>{{employee.designation}}</td>
            <td>{{employee.joiningDate}}</td>
            <td>
                <button >update</button>
                <button >update</button>
                <button >update</button>
            </td>
        </tr>
    </tbody>


</table>
